// Loop through all incoming items
// 'items' is the global variable in n8n Code nodes containing input data
const mergedBinary = {};
const summary = [];

items.forEach((item, index) => {
  if (item.binary) {
    Object.keys(item.binary).forEach((key) => {
      // Create a unique key for the new single item.
      // We append the index (e.g., 'data_0', 'data_1') so that 
      // files don't overwrite each other if they share the generic name "data".
      const newKey = `${key}_${index}`;

      mergedBinary[newKey] = item.binary[key];

      // Optional: Add metadata to JSON output for reference
      summary.push({
        originalKey: key,
        newKey: newKey,
        fileName: item.binary[key].fileName
      });
    });
  }
});

// Return exactly one item containing all binary objects
return [
  {
    json: {
      message: "Successfully merged binary items",
      fileCount: Object.keys(mergedBinary).length,
      files: summary
    },
    binary: mergedBinary
  }
];

// !!! IMPORTANT INSTRUCTION !!!
// Do NOT paste this code into the "Email" node.
// This code must go into a "Code" node set to "Run Once for All Items".
// The Output of this node (html_body) will then be used in the Email node.

// 1. Initialize an empty string for the table rows
let emailRows = '';

// 2. Loop through every quote found in the previous node
// We use 'items' which is the n8n global variable for all input data
items.forEach((item, index) => {
  const quote = item.json;

  // Safety check: Ensure we have a premium to format
  const premiumValue = quote.quoted_premium || 0;

  // Format the premium as currency (USD)
  const formattedPremium = new Intl.NumberFormat('en-US', {
    style: 'currency',
    currency: 'USD'
  }).format(premiumValue);

  let AddedCoverage = 'N/A';
  if (index === 1) AddedCoverage = 'PIP';

  // Construct the selection URL with your specific Webhook
  // We append the quote_id and customer_id as query parameters so your receiving workflow can use them.
  const selectionUrl = `
https://policyflow.app.n8n.cloud/webhook/90cfb756-469a-43e2-95fc-7da821a6b650?quote_id=${quote.quote_id}&customer_id=${quote.customer_id}&policy_id=${quote.original_policy_id}&Premium=${quote.quoted_premium}&carrier_name=${quote.carrier_name}`;

  // Build the HTML for this specific row
  emailRows += `
    <tr>
      <td style="padding: 12px; border-bottom: 1px solid #eeeeee;">
        <strong style="font-size: 16px; color: #333;">${quote.carrier_name || 'Unknown Carrier'}</strong>
        ${quote.broker_recommendation === 2 ? '<br><span style="color: green; font-weight: bold; font-size: 10px;">â˜… RECOMMENDED</span>' : ''}
      </td>
      <td style="padding: 12px; border-bottom: 1px solid #eeeeee; color: #555;">
        ${quote.quote_id}
      </td>
      <td style="padding: 12px; border-bottom: 1px solid #eeeeee; color: #555;">
        ${quote.quoted_coverage || 'N/A'}
      </td>
      <td style="padding: 12px; border-bottom: 1px solid #eeeeee; color: #555;">
        ${quote.quoted_deductible || 'N/A'}
      </td>
      <td style="padding: 12px; border-bottom: 1px solid #eeeeee; color: #555;">
        ${AddedCoverage}
      </td>
      <td style="padding: 12px; border-bottom: 1px solid #eeeeee; font-weight: bold;">
        ${quote.quoted_premium}
      </td>
      <td style="padding: 12px; border-bottom: 1px solid #eeeeee; text-align: center;">
        <a href="${selectionUrl}" target="_blank" style="background-color: #007bff; color: #ffffff; padding: 8px 12px; text-decoration: none; border-radius: 4px; font-weight: bold; font-size: 14px;">Select</a>
      </td>
    </tr>
  `;
});

// 3. Construct the email HTML fragment (Removed <html>/<body> tags to prevent empty email issues)
const emailHtml = `
<div style="font-family: Arial, sans-serif; color: #333; width: 100%; max-width: 600px; margin: 0 auto;">
  <h3>Insurance Quotes Available</h3>
  <p>Hello,</p>
  <p>We found ${items.length} quotes for your review based on your request.</p>

  <table width="100%" border="0" cellspacing="0" cellpadding="0">
    <thead style="background-color: #f8f9fa;">
      <tr>
        <th style="padding: 12px; text-align: left; border-bottom: 2px solid #dee2e6;">Provider</th>
        <th style="padding: 12px; text-align: left; border-bottom: 2px solid #dee2e6;">Quote ID</th>
        <th style="padding: 12px; text-align: left; border-bottom: 2px solid #dee2e6;">Coverage(Comprehensive)</th>
        <th style="padding: 12px; text-align: left; border-bottom: 2px solid #dee2e6;">Deductible</th>
        <th style="padding: 12px; text-align: left; border-bottom: 2px solid #dee2e6;">AddedCoverage</th>
        <th style="padding: 12px; text-align: left; border-bottom: 2px solid #dee2e6;">Premium</th>
        <th style="padding: 12px; text-align: center; border-bottom: 2px solid #dee2e6;">Action</th>
      </tr>
    </thead>
    <tbody>
      ${emailRows}
    </tbody>
  </table>

  <p style="margin-top: 20px;">Please select the option that best fits your needs.</p>
  <p>Best regards,<br>Your Insurance Team</p>
</div>
`;

// 4. Output the result
// This creates a single item with a field called 'html_body'
return [{
  json: {
    subject: `Insurance Quotes for ${items[0].json.customer_id}`,
    html_body: emailHtml
  }
}];

