const text = $json.text || "";

// Normalize line breaks and spacing
const cleanText = text.replace(/\r?\n|\r/g, ' ').replace(/\s+/g, ' ');

// Match "Policy Number:" followed by a POL-style ID
const policyMatch = cleanText.match(/Policy Number:\s*(POL-\d{4}-\d{6})/i);

// Fallback: grab all POL-style IDs and assume second one is policy number
const allPols = [...text.matchAll(/POL-\d{4}-\d{6}/g)];
const fallbackPolicy = allPols.length >= 2 ? allPols[1][0] : allPols[0]?.[0] || null;

// Final result: prefer labeled match, fallback to second POL
const policyNumber = policyMatch?.[1] || fallbackPolicy;

return [
  {
    policy_number: policyNumber,
  },
];
